CREATE PROC [dbo].[AddEntityBatchHistory]	@IntegrationId uniqueidentifier,	@JobInstanceId uniqueidentifier,	@JobStepInstanceId uniqueidentifier,	@DataSourceId uniqueidentifier,	@SyncSide char(6),	@TechnicalEntityName nvarchar(100),	@UserFriendlyEntityName nvarchar(100),	@EntityKeyFields nvarchar(100)AS	SET NOCOUNT ON	INSERT INTO EntityBatchHistory (		[IntegrationId],		[JobInstanceId],		[JobStepInstanceId],		[DataSourceId],		[SyncSide],		[CreatedDate],		[TechnicalEntityName],		[UserFriendlyEntityName],		[EntityKeyFields]	)	VALUES (		@IntegrationId,		@JobInstanceId,		@JobStepInstanceId,		@DataSourceId,		@SyncSide,		GETDATE(),		@TechnicalEntityName,		@UserFriendlyEntityName,		@EntityKeyFields	)	SELECT SCOPE_IDENTITY() As InsertedID