CREATE PROC [dbo].[AddEntityBatchRecordHistory]	@EntityBatchHistoryId int,	@Action char(1),	@EntityKeyValues nvarchar(250),	@HasError bit,	@ErrorMessage nvarchar(1000)AS	SET NOCOUNT ON	INSERT INTO EntityBatchRecordHistory (		[EntityBatchHistoryId],		[CreatedDate],		[Action],		[EntityKeyValues],		[HasError],		[ErrorMessage]	)	VALUES (		@EntityBatchHistoryId,		GETDATE(),		@Action,		@EntityKeyValues,		@HasError,		@ErrorMessage	)	SELECT SCOPE_IDENTITY() As InsertedID