CREATE PROC [dbo].[AddEntityBatchRecordHistoryDetail]	@EntityBatchRecordHistoryId int,	@FieldName nvarchar(250),	@FieldCaption nvarchar(500),	@OldValue nvarchar(MAX),	@NewValue nvarchar(MAX)AS	SET NOCOUNT ON	INSERT INTO EntityBatchRecordHistoryDetail (		[EntityBatchRecordHistoryId],		[CreatedDate],		[FieldName],		[FieldCaption],		[OldValue],		[NewValue]	)	VALUES (		@EntityBatchRecordHistoryId,		GETDATE(),		@FieldName,		@FieldCaption,		@OldValue,		@NewValue	)